package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.WfInstanceModel;
import org.ebs.services.to.Input.WfInstanceInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:03:02 AM
 */
@Component
  class WfInstanceConverterInput implements Converter<WfInstanceInput,WfInstanceModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public WfInstanceModel convert(WfInstanceInput source){
		WfInstanceModel target = new  WfInstanceModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}