package org.ebs.services.converter;

import org.ebs.model.WorkflowModel;
import org.ebs.services.to.WorkflowTo;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:03:03 AM
 */
@Component
  class WorkflowConverter implements Converter<WorkflowModel,WorkflowTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public WorkflowTo convert(WorkflowModel source){
		WorkflowTo target = new  WorkflowTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}