package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.WorkflowModel;
import org.ebs.services.to.Input.WorkflowInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Jul-2020 5:07:38 PM
 */
@Component
  class WorkflowConverterInput implements Converter<WorkflowInput,WorkflowModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public WorkflowModel convert(WorkflowInput source){
		WorkflowModel target = new  WorkflowModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}