package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.WorkflowEventModel;
import org.ebs.services.to.WorkflowEventTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 07-Aug-2020 3:02:25 PM
 */
@Component
  class WorkflowEventConverter implements Converter<WorkflowEventModel,WorkflowEventTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public WorkflowEventTo convert(WorkflowEventModel source){
		WorkflowEventTo target = new  WorkflowEventTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}