package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.WorkflowNodeModel;
import org.ebs.services.to.Input.WorkflowNodeInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 09-Aug-2020 4:52:01 PM
 * modified by Jorge 2020-08-19
 */
@Component
  class WorkflowNodeInputConverter implements Converter<WorkflowNodeInput,WorkflowNodeModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public WorkflowNodeModel convert(WorkflowNodeInput source){
		WorkflowNodeModel target = new  WorkflowNodeModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}