package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.TeamModel;
import org.ebs.services.to.Input.TeamInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 20-Aug-2020 1:06:37 PM
 */
@Component
  class TeamConverterInput implements Converter<TeamInput,TeamModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public TeamModel convert(TeamInput source){
		TeamModel target = new  TeamModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}